import React, { useState } from 'react';

const InsertIntoDataBase = () => {
    const [formData, setFormData] = useState({
        field1: '',
        field2: '',
        field3: '',
        field4: '',
        field5: '',
        field6: '',
        field7: '',
        field8: '',
        field9: ''
    });

    const formStyles = {
        display: 'flex',
        flexWrap: 'wrap',
        gap: '20px'
    };

    const formGroupStyles = {
        display: 'flex',
        flexDirection: 'column',
        flex: '1 1 30%', // Allow the div to grow and shrink, and take up 30% of the container's width
        minWidth: '200px' // Ensure a minimum width for smaller screens
    };

    const labelStyles = {
        marginBottom: '5px'
    };

    const inputStyles = {
        padding: '8px',
        border: '1px solid #ccc',
        borderRadius: '4px',
        width: '100%' // Make the input take up the full width of the div
    };

    const buttonStyles = {
        padding: '10px 20px',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
        marginTop: '20px'
    };

    const buttonHoverStyles = {
        backgroundColor: '#0056b3'
    };

    const h1Styles = {
        fontSize: '1.5em',
        fontFamily: 'Arial, sans-serif',
        color: '#333',
        textAlign: 'center',
        margin: '20px 0',
        fontWeight: 'bold'
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        useEffect(() => {
            const fetchSalesData = async () => {
                try {
                    const response = await fetch('http://0.0.0.0:4000/api/sales');
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    const data = await response.json();
                    setSalesData(data);
                } catch (error) {
                    console.error('Error fetching sales data:', error);
                }
            };

            fetchSalesData();
        }, []);


    };

    const labelNames = ["Brand", "Model", "Year", "Price", "Movement", "Case Material", "Bracelet Material", "Condition", "Box and Papers"];

    return (
        <div>
            <h1 style={h1Styles}> Insert Data Into Database</h1>
            <form onSubmit={handleSubmit} style={formStyles}>
                <div style={formGroupStyles}>
                    {labelNames.map((label, index) => (
                        <div key={index}>
                            <label style={labelStyles}>{label}</label>
                            <input type="text" name={`field${index + 1}`} value={formData[`field${index + 1}`]} onChange={handleChange} style={inputStyles} />
                        </div>
                    ))}
                </div>
                <button
                    type="submit"
                    style={buttonStyles}
                    onMouseEnter={(e) => e.target.style.backgroundColor = buttonHoverStyles.backgroundColor}
                    onMouseLeave={(e) => e.target.style.backgroundColor = buttonStyles.backgroundColor}
                >
                    Submit
                </button>
            </form>
        </div>
    );
};

export default InsertIntoDataBase;
