import React, { useEffect, useState } from 'react';
import './SalesTable.css';

const SalesTable = () => {
    const [salesData, setSalesData] = useState([]);

    useEffect(() => {
        const fetchSalesData = async () => {
            try {
                const response = await fetch('0/api/sales');
                const data = await response.json();
                setSalesData(data);
            } catch (error) {
                console.error('Error fetching sales data:', error);
            }
        };

        fetchSalesData();
    }, []);

    return (
        <table>
            <thead>
                <tr>
                    <th>Item</th>
                    <th>Type</th>
                    <th>Price</th>
                </tr>
            </thead>
            <tbody>
                {salesData.map((sale, index) => (
                    <tr key={index} className={sale.type === 'buy' ? 'buy' : 'sell'}>
                        <td>{sale.item}</td>
                        <td>{sale.type}</td>
                        <td>{sale.price}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default SalesTable;
