import React, { useEffect, useState } from 'react';
import './SalesTable.css';


const getTableStyles = () => {
    return `
        table {
            width: 100%;
            border-collapse: collapse;
        }
        th, td {
            border: 1px solid #ddd;
            padding: 8px;
        }
        th {
            background-color: #f2f2f2;
            text-align: left;
        }
        tr:nth-child(even) {
            background-color: #f9f9f9;
        }
        tr:hover {
            background-color: #ddd;
        }
    `;
};

const SalesTable = () => {
    const [salesData, setSalesData] = useState([[0]]);
    console.log('This is the salesData:', salesData[0][0]);

    useEffect(() => {
        const fetchSalesData = async () => {
            try {
                const response = await fetch('http://0.0.0.0:4000/api/sales');
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setSalesData(data);
            } catch (error) {
                console.error('Error fetching sales data:', error);
            }
        };

        fetchSalesData();
    }, []);

    return (
        <div style={getTableStyles()}>
            <table>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Type</th>
                        <th>Movement</th>
                        <th>Action</th>
                        <th>Price</th>
                        <th>Box</th>
                        <th>Papers</th>
                        <th>Year</th>
                        <th>Date bought</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{salesData[0][0]}</td>
                        <td>{salesData[0][1]}</td>
                        <td>{salesData[0][2]}</td>
                        <td>{salesData[0][3]}</td>
                        <td>{salesData[0][4]}</td>
                        <td>{salesData[0][5]}</td>
                        <td>{salesData[0][6]}</td>
                        <td>{salesData[0][7]}</td>
                        <td>{salesData[0][8]}</td>
                    </tr>
                </tbody>
            </table>
        </div>
    );
};

export default SalesTable;
